$Shares = Import-Csv -Path "D:\Shares.csv"#need to do this so the date can match correctly to lastwritetime$lowerBound = [datetime]::ParseExact("12/04/2021", "MM/dd/yyyy", [CultureInfo]::InvariantCulture)$upperBound = [datetime]::ParseExact("01/20/2022", "MM/dd/yyyy", [CultureInfo]::InvariantCulture)Foreach ($_ in $Shares) {Get-Childitem (Join-Path $_.ShareName "\*") -file -recurse -Filter *.pdf | Where-Object {$_.LastWriteTime -ge $lowerBound -and $_.LastWriteTime -le $upperBound} | ForEach-Object {Copy-Item $_.FullName (Join-Path "D:\PDFs\" $_.Name); echo $_}}