#need to do this so the date can match correctly to lastwritetime$upperBound = [datetime]::ParseExact("12/31/2018", "MM/dd/yyyy", [CultureInfo]::InvariantCulture)$lowerBound = [datetime]::ParseExact("01/01/2018", "MM/dd/yyyy", [CultureInfo]::InvariantCulture)Get-ChildItem "\\msidc1\CMM Files\workarea\results" -file | Where-Object {$_.LastWriteTime -ge $lowerBound -and $_.LastWriteTime -le $upperBound} | ForEach-Object {Move-Item $_.FullName (Join-Path "\\msidc1\CMM Files\workarea\results\Results 2018\" $_.Name); echo $_}